var link=document.querySelector(".modal-open"),popup=document.querySelector(".modal-write-us"),close=document.querySelector(".modal-close"),username=popup.querySelector("[name=name]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&popup.classList.remove("modal-show")});